DROP TABLE IF EXISTS `cms_portal`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `cms_portal` (
  `id` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `cmsident` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `meta_seperator_hidden` varchar(255) NOT NULL DEFAULT 'Meta Daten',
  `meta_description` varchar(200) NOT NULL COMMENT 'Kurzbeschreibung: Die Kurzbeschreibung wird von vielen Suchmaschinen genutzt, um Ihre Seite zu beschreiben. Sie sollten wichtige Suchbegriffe in den Text einbinden. Maximale Länge 200 Zeichen!',
  `meta_keywords` longtext NOT NULL COMMENT 'Search terms: Enter the search terms for your page, separated by commas. Avoid the repetition of key words as this can lead to search engines interpreting it as spam and not indexing the page. It is recommended to use not more than 10 to 15 words.\nMoreove',
  `meta_author` varchar(255) NOT NULL COMMENT 'Verfasser: Geben Sie hier den Namen des Verfassers der Seite an.',
  `meta_publisher` varchar(255) NOT NULL COMMENT 'Publisher: Enter the name of the publisher of the page.',
  `main_node_tree` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL COMMENT 'Navigationsstartpunkt',
  `images` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL DEFAULT '1' COMMENT 'Logo: ',
  `meta_headline` varchar(255) NOT NULL DEFAULT 'META Daten',
  `custom_metadata` longtext NOT NULL COMMENT 'Eigene Metadaten: Daten, die Sie hier eingeben, werden 1:1 in den Header aller Seiten dieses Portals übernommen. Sie können dieses Feld also z.B. verwenden, um die Verify-ID von Google als Meta-Tag in die Seite aufzunehmen.',
  `use_googlesitemap` enum('0','1') NOT NULL DEFAULT '1' COMMENT 'Google sitemap: Specify if a google sitemap should be created for this portal',
  `home_node_id` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL COMMENT 'Homeseite des Portals',
  `identifier` varchar(255) NOT NULL COMMENT 'Identifier / prefix: This identifier enables you to differentiate between several portals on the same domain. The identifier is attached to the domain, e. g."https://mein-shop.de/portal1/Warenkorb/" for the identifier "portal1" on the shopping cart page.',
  `sort_order` int(11) NOT NULL COMMENT 'Sorting: If more than one portal is placed on the same domain, you can use sorting to control which portal is used for a call without identifier.',
  `cms_language_id` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL DEFAULT '22' COMMENT 'Sprache des Portals: ',
  `index_search` enum('0','1') NOT NULL DEFAULT '1' COMMENT 'Include in search index generation: You can specify here if the portal should be indexed when the search indexer is started.',
  `google_analytic_number` varchar(255) NOT NULL COMMENT 'Google Analytics ID: Google analytics gives detailed information about the visitors of your page. More information you find on http://www.google.com/analytics.',
  `cms_locals_id` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL DEFAULT '1' COMMENT 'Gebietsschema: ',
  `page_not_found_node` char(36) NOT NULL COMMENT '404-Page-Not-Found-Seite: ',
  `use_slash_in_seo_urls` enum('0','1') NOT NULL DEFAULT '0' COMMENT '/ anstatt .html in SEO URLs verwenden: ',
  `etracker_id` varchar(255) NOT NULL COMMENT 'etracker ID: Enter your etracker ID to enable tracking for this portal. http://www.etracker.de',
  `title` varchar(255) NOT NULL COMMENT 'Portal title: Specify the text to be displayed in the title tag of the website.',
  `watermark_logo` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL DEFAULT '1' COMMENT 'Logo for watermarking: The logo has to be in PNG or JPG format and adds a watermark to images and videos.',
  `deactive_portal` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'Deactivate portal: If YES, the portal is only visible for users logged into the CMS at the same time.',
  `wysiwyg_css_url` varchar(255) NOT NULL COMMENT 'WYSIWYG Text-Editor CSS URL: ',
  `ivw_id` varchar(255) NOT NULL COMMENT 'IVW-Angebotskennung: ',
