DROP TABLE IF EXISTS `data_extranet`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `data_extranet` (
  `id` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL,
  `cmsident` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `sessionlife` int(11) NOT NULL DEFAULT 3600 COMMENT 'Session lifetime (in seconds): ',
  `access_refused_node_id` char(36) NOT NULL COMMENT 'Access denied (not signed in): This page is launched, if the user is not logged in and the page is secured.',
  `forgot_password_treenode_id` char(36) NOT NULL COMMENT 'Forgot password: ',
  `forgot_pwd_seperator` varchar(255) NOT NULL DEFAULT 'Passwort-Vergessen Konfiguration' COMMENT 'Passwort-Vergessen Konfiguration: ',
  `fpwd_intro` text NOT NULL COMMENT 'Einleitung: ',
  `fpwd_end` text NOT NULL COMMENT 'Abschluss: ',
  `fpwd_title` varchar(255) NOT NULL COMMENT 'Titel: ',
  `noaccess` varchar(255) NOT NULL DEFAULT 'zugriff-verweigert Konfiguration' COMMENT 'zugriff-verweigert Konfiguration: ',
  `noaccess_title` varchar(255) NOT NULL COMMENT 'Titel: ',
  `noaccess_text` longtext NOT NULL COMMENT 'Text: ',
  `login_success_node_id` char(36) NOT NULL COMMENT 'Login successful: ',
  `node_confirm_registration` char(36) NOT NULL COMMENT 'Confirm registration: Target page for the Double-Opt-In registration',
  `registration_success` longtext NOT NULL COMMENT 'Registration successful: ',
  `registration_failed` longtext NOT NULL COMMENT 'Registration failed: ',
  `node_login_id` char(36) NOT NULL COMMENT 'Login: ',
  `node_register_id` char(36) NOT NULL COMMENT 'Registration: ',
  `node_registration_success_id` char(36) NOT NULL COMMENT 'Registration successful: After having registered successfully, the user will be redirected to this page.',
  `extranet_spot_name` varchar(255) NOT NULL COMMENT 'Name of the spot where an extranet module is available: Since login / register requests etc. must be sent to an extranet module, but the forms may be displayed via another module, the system must know to which slot the requests can be sent.',
  `user_must_confirm_registration` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'Users must confirm their registration: ',
  `cms_portal_id` char(36) CHARACTER SET latin1 COLLATE latin1_general_ci NOT NULL COMMENT 'Konfiguration des Portals: ',
  `community_post_registration_info` longtext NOT NULL COMMENT 'Text to be displayed after login to the community: ',
  `login_is_email` enum('0','1') NOT NULL DEFAULT '1' COMMENT 'Login must be an email address: If the value is set to YES, the login name will be checked for validity (to see whether the email address is valid).',
  `fpwd_email__en` text NOT NULL COMMENT 'translation Englisch',
  `fpwd_email_subject__en` varchar(255) NOT NULL COMMENT 'translation Englisch',
  `login_allowed_not_confirmed_user` enum('0','1') NOT NULL DEFAULT '0' COMMENT 'Enable login for non-confirmed users: If YES, also non-confirmed users can log in. \nNon-confirmed users may have restricted access to pages (configurable in the page settings) and functions.',
  `use_save_forgot_password` enum('0','1') NOT NULL DEFAULT '1' COMMENT 'Use forgot password, get new password method: If YES, the user receives an email with a link that leads to a form. The user can enter a new password here.\nThis link is valid for 2 hours. The old password remains in place if the user does not change his pa',
  `password_change_key_time_validity` int(11) NOT NULL DEFAULT 2 COMMENT 'Validity of the password change key (in hours): Specifies how long a password change key is valid after a request (0 and -1 mean unlimited validity).',
  `group_right_denied_node_id` char(36) NOT NULL COMMENT 'Access denied (group permissons): This page is launched, if an extranet user is logged in, but does not have the appropriate group rights.',
  `logout_success_node_id` char(36) NOT NULL COMMENT 'Logout successful: This page is launched after a successful logout, e.g. the home page.',
  `node_my_account_cms_tree_id` char(36) NOT NULL COMMENT 'My account: ',
  `fpwd_intro__de` text NOT NULL COMMENT 'translation Deutsch',
  `fpwd_end__de` text NOT NULL COMMENT 'translation Deutsch',
  `fpwd_title__de` varchar(255) NOT NULL COMMENT 'translation Deutsch',
  `noaccess_title__de` varchar(255) NOT NULL COMMENT 'translation Deutsch',
  `noaccess_text__de` longtext NOT NULL COMMENT 'translation Deutsch',
  `registration_success__de` longtext NOT NULL COMMENT 'translation Deutsch',
  `registration_failed__de` longtext NOT NULL COMMENT 'translation Deutsch',
  PRIMARY KEY (`id`),
  UNIQUE KEY `cmsident` (`cmsident`),
  KEY `user_must_confirm_registration` (`user_must_confirm_registration`),
  KEY `node_my_account_cms_tree_id` (`node_my_account_cms_tree_id`),
  KEY `sessionlife` (`sessionlife`),
  KEY `cms_portal_id` (`cms_portal_id`),
  KEY `login_allowed_not_confirmed_user` (`login_allowed_not_confirmed_user`),
  KEY `login_success_node_id` (`login_success_node_id`),
  KEY `node_registration_success_id` (`node_registration_success_id`),
  KEY `forgot_password_treenode_id` (`forgot_password_treenode_id`),
  KEY `access_refused_node_id` (`access_refused_node_id`),
  KEY `group_right_denied_node_id` (`group_right_denied_node_id`),
  KEY `logout_success_node_id` (`logout_success_node_id`),
  KEY `node_register_id` (`node_register_id`),
  KEY `node_login_id` (`node_login_id`),
  KEY `extranet_spot_name` (`extranet_spot_name`),
  KEY `login_is_email` (`login_is_email`),
  KEY `use_save_forgot_password` (`use_save_forgot_password`),
  KEY `password_change_key_time_validity` (`password_change_key_time_validity`),
  KEY `node_confirm_registration` (`node_confirm_registration`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='Extranet-Konfiguration:\nKonfiguration des Extranet-Moduls';
