INSERT INTO `pkg_shop_statistic_group` VALUES ('07f209fa-2b9b-11df-9c53-00fcefbad5fb',2,'Number of ordererd items',2,'shop_payment_method_name','        SELECT [{sColumnName}] AS sColumnName,\n               `shop_order_item`.`order_amount` AS dColumnValue,\n               shop_payment_method_name\n          FROM `shop_order`\n     LEFT JOIN `shop_order_item` ON `shop_order`.`id` = `shop_order_item`.`shop_order_id`\n               [{sCondition}]\n           AND `shop_order`.`canceled` = \'0\'\n      ORDER BY datecreated\n  ','`shop_order`.`datecreated`','`shop_order`.`cms_portal_id`','Anzahl bestellter Artikel'),('5960bc8c-2b9b-11df-9c53-00fcefbad5fb',6,'Categories (sales)',2,'categorypath,shop_payment_method_name','        SELECT [{sColumnName}] AS sColumnName,\n               `shop_order_item`.`order_amount` AS totalordered,\n               `shop_order_item`.`order_price_after_discounts` AS dColumnValue,\n               shop_payment_method_name,\n               `shop_category`.`url_path` AS categorypath,\n               `shop_order_item`.*\n          FROM `shop_order_item`\n     LEFT JOIN `shop_order` ON `shop_order_item`.`shop_order_id` = `shop_order`.`id`\n     LEFT JOIN `shop_article_shop_category_mlt` ON `shop_order_item`.`shop_article_id` = `shop_article_shop_category_mlt`.`source_id`\n     LEFT JOIN `shop_category` ON `shop_article_shop_category_mlt`.`target_id` = `shop_category`.`id`\n               [{sCondition}]\n           AND `shop_order`.`canceled` = \'0\'\n     ORDER BY  [{sColumnName}]\n  ','`shop_order`.`datecreated`','`shop_order`.`cms_portal_id`','Kategorien (Verkäufe)'),('6e4af164-8792-9e01-573b-4bb36fd68cdc',3,'Number of orders',3,'','        SELECT [{sColumnName}] AS sColumnName,\n               1 AS dColumnValue\n          FROM `shop_order`\n               [{sCondition}]\n           AND `shop_order`.`canceled` = \'0\'\n      ORDER BY datecreated\n  ','`shop_order`.`datecreated`','`shop_order`.`cms_portal_id`','Anzahl Bestellungen'),('74292e9c-2b9a-11df-9c53-00fcefbad5fb',1,'Revenue excluding shipping',1,'shop_payment_method_name','        SELECT [{sColumnName}] AS sColumnName,\n               (`shop_order_item`.`price_discounted` * `shop_order_item`.`order_amount`) AS dColumnValue,\n               shop_payment_method_name\n          FROM `shop_order`\n     LEFT JOIN `shop_order_item` ON `shop_order`.`id` = `shop_order_item`.`shop_order_id`\n               [{sCondition}]\n           AND `shop_order`.`canceled` = \'0\'\n      ORDER BY datecreated\n  ','`shop_order`.`datecreated`','`shop_order`.`cms_portal_id`','Umsatz ohne Versand'),('c3bd7230-effb-3083-50a5-f495c34f7283',4,'Ø basket size',3,'','        SELECT [{sColumnName}] AS sColumnName,\n               (SUM(`shop_order`.`count_articles`)/COUNT(`shop_order`.`id`)) AS dColumnValue\n          FROM `shop_order`\n               [{sCondition}]\n           AND `shop_order`.`canceled` = \'0\'\n      GROUP BY [{sColumnName}]\n      ORDER BY datecreated\n  ','`shop_order`.`datecreated`','`shop_order`.`cms_portal_id`','Ø Warenkorb Größe'),('d577de75-6494-9075-15b7-7cb887d7b065',5,'Ø basket value excl. shipping',3,'','        SELECT [{sColumnName}] AS sColumnName,\n               (SUM(`shop_order`.`value_article` - `shop_order`.`value_discounts`)/COUNT(`shop_order`.`id`)) AS dColumnValue\n          FROM `shop_order`\n               [{sCondition}]\n           AND `shop_order`.`canceled` = \'0\'\n      GROUP BY [{sColumnName}]\n      ORDER BY datecreated\n  ','`shop_order`.`datecreated`','`shop_order`.`cms_portal_id`','Ø Warenkorb Wert ohne Versand');
